// helper to read profile from localStorage or form inputs
function getProfile() {
  try {
    const p = JSON.parse(localStorage.getItem('shoe_profile') || '{}');
    // prefer saved profile, but if form fields are present and filled use them
    const formName = profileName && profileName.value && profileName.value.trim();
    if(formName) {
      return {
        name: formName,
        email: profileEmail ? profileEmail.value.trim() : (p.email || ''),
        phone: profilePhone ? profilePhone.value.trim() : (p.phone || ''),
        address: profileAddress ? profileAddress.value.trim() : (p.address || '')
      };
    }
    return {
      name: p.name || '',
      email: p.email || '',
      phone: p.phone || '',
      address: p.address || ''
    };
  } catch (e) {
    return { name:'', email:'', phone:'', address:'' };
  }
}

// show year when element exists (set later after DOM ready)

// Render product cards
function renderProducts(list) {
  productGrid.innerHTML = '';
  list.forEach(prod => {
    const col = document.createElement('div');
    col.className = 'col-12 col-sm-6 col-lg-4';
    col.innerHTML = `
      <div class="card card-product h-100 shadow-sm">
        <img src="${prod.img}" class="card-img-top product-img" alt="${escapeHtml(prod.title)}">
        <div class="card-body d-flex flex-column">
          <h5 class="card-title small">${escapeHtml(prod.title)}</h5>
          <div class="mt-auto d-flex justify-content-between align-items-center">
            <div>
              <span class="badge badge-price rounded-pill px-3">₹${prod.price}</span>
            </div>
            <div>
              <button class="btn btn-sm btn-outline-secondary me-2 btn-details" data-id="${prod.id}">Details</button>
              <button class="btn btn-sm btn-primary btn-add" data-id="${prod.id}">Add</button>
            </div>
          </div>
        </div>
      </div>
    `;
    productGrid.appendChild(col);

    // Attach event listeners to avoid relying on inline onclick handlers
    const detailsBtn = col.querySelector('.btn-details');
    const addBtn = col.querySelector('.btn-add');
    if(detailsBtn) detailsBtn.addEventListener('click', () => viewDetails(prod.id));
    if(addBtn) addBtn.addEventListener('click', () => addToCart(prod.id));
  });

  shownCount.textContent = list.length;
}

// Simple escape for titles (safe enough for demo)
function escapeHtml(s) {
  return String(s).replace(/[&<>"']/g, function(m) {
    return ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'}[m]);
  });
}

// -----------------
// Simple auth (localStorage) — supports up to 3 users
// -----------------
// localStorage keys:
// - 'shoe_users' => array [{name,email,password}]
// - 'shoe_current_user' => email string of logged-in user

function loadUsers() {
  try { return JSON.parse(localStorage.getItem('shoe_users') || '[]'); } catch(e) { return []; }
}
function saveUsers(users) {
  localStorage.setItem('shoe_users', JSON.stringify(users || []));
}
function getCurrentUserEmail() {
  return localStorage.getItem('shoe_current_user') || null;
}
function setCurrentUser(email) {
  if(email) localStorage.setItem('shoe_current_user', email); else localStorage.removeItem('shoe_current_user');
}
function findUserByEmail(email) {
  if(!email) return null;
  const users = loadUsers();
  return users.find(u => u.email.toLowerCase() === email.toLowerCase()) || null;
}

function registerUser(name, email, password) {
  const users = loadUsers();
  if(users.length >= 3) return { ok:false, msg: 'Maximum of 3 users allowed (demo).' };
  if(!name || !email || !password) return { ok:false, msg: 'Please fill all fields.' };
  if(findUserByEmail(email)) return { ok:false, msg: 'Email already registered.' };
  const user = { name: name.trim(), email: email.trim().toLowerCase(), password: String(password) };
  users.push(user);
  saveUsers(users);
  // auto-login newly registered user
  setCurrentUser(user.email);
  return { ok:true, user };
}

function loginUser(email, password) {
  if(!email || !password) return { ok:false, msg: 'Please fill both email and password.' };
  const user = findUserByEmail(email);
  if(!user) return { ok:false, msg: 'No account found for this email.' };
  if(user.password !== String(password)) return { ok:false, msg: 'Incorrect password.' };
  setCurrentUser(user.email);
  return { ok:true, user };
}

function logoutUser() {
  setCurrentUser(null);
}

// update UI bits (auth welcome, auth button text)
function updateAuthUI() {
  const email = getCurrentUserEmail();
  const authBtnEl = document.getElementById('authBtn');
  const authWelcomeEl = document.getElementById('authWelcome');
  const authUserNameEl = document.getElementById('authUserName');
  const authUserEmailEl = document.getElementById('authUserEmail');
  if(email) {
    const user = findUserByEmail(email) || {};
    if(authBtnEl) authBtnEl.textContent = `Hi, ${user.name || user.email}`;
    if(authWelcomeEl) authWelcomeEl.style.display = 'block';
    if(authUserNameEl) authUserNameEl.textContent = user.name || '—';
    if(authUserEmailEl) authUserEmailEl.textContent = user.email || '—';
  } else {
    if(authBtnEl) authBtnEl.textContent = 'Login / Register';
    if(authWelcomeEl) authWelcomeEl.style.display = 'none';
    if(authUserNameEl) authUserNameEl.textContent = '';
    if(authUserEmailEl) authUserEmailEl.textContent = '';
  }
}


function viewDetails(id) {
  const p = products.find(x => x.id === id);
  if(!p) return;
  // simple alert for demo
  alert(`${p.title}\n\nCategory: ${p.category}\nPrice: ₹${p.price}`);
}

function addToCart(id) {
  cart[id] = (cart[id] || 0) + 1;
  updateCartUI();
  // show toast feedback
  if(addToast) addToast.show();
}

// remove one
function removeFromCart(id) {
  if(!cart[id]) return;
  cart[id]--;
  if(cart[id] <= 0) delete cart[id];
  updateCartUI();
}

// completely remove
function deleteFromCart(id) {
  if(cart[id]) delete cart[id];
  updateCartUI();
}

function updateCartUI() {
  // update count
  const totalItems = Object.values(cart).reduce((s, q) => s + q, 0);
  if(cartCount) cartCount.textContent = totalItems;
  if(topCartCountEl) topCartCountEl.textContent = totalItems;

  // render list
  cartItems.innerHTML = '';
  let total = 0;
  if(totalItems === 0) {
    cartItems.innerHTML = `<div class="text-center text-muted py-4">Your cart is empty.</div>`;
  } else {
    for (const [idStr, qty] of Object.entries(cart)) {
      const id = Number(idStr);
      const prod = products.find(p => p.id === id);
      const line = document.createElement('div');
      line.className = 'list-group-item d-flex align-items-center';
      line.innerHTML = `
        <img src="${prod.img}" alt="" style="width:64px;height:48px;object-fit:cover" class="rounded me-3">
        <div class="flex-grow-1">
          <div class="fw-semibold">${escapeHtml(prod.title)}</div>
          <small class="text-muted">₹${prod.price} × ${qty} = ₹${prod.price * qty}</small>
        </div>
        <div class="d-flex gap-2 align-items-center ms-3">
          <button class="btn btn-sm btn-outline-secondary" onclick="removeFromCart(${id})">-</button>
          <div class="px-2">${qty}</div>
          <button class="btn btn-sm btn-outline-secondary" onclick="addToCart(${id})">+</button>
          <button class="btn btn-sm btn-danger ms-2" onclick="deleteFromCart(${id})">Remove</button>
        </div>
      `;
      cartItems.appendChild(line);
      total += prod.price * qty;
    }
  }
  cartTotal.textContent = total;
  // also update checkout modal values
  checkoutTotal.textContent = total;
  computeFinalPriceAndApplyOffer();
}

// Offer countdown (10 minutes for demo)
let offerExpiresAt = null;
function startOfferCountdown(durationSec = 600) {
  offerExpiresAt = Date.now() + durationSec * 1000;
  updateOfferTimer();
  setInterval(updateOfferTimer, 1000);
}

function updateOfferTimer() {
  if(!offerTimer || !offerExpiresAt) return;
  const diff = Math.max(0, Math.floor((offerExpiresAt - Date.now()) / 1000));
  const hh = String(Math.floor(diff/3600)).padStart(2,'0');
  const mm = String(Math.floor((diff%3600)/60)).padStart(2,'0');
  const ss = String(diff%60).padStart(2,'0');
  offerTimer.textContent = `${hh}:${mm}:${ss}`;
  if(diff === 0) {
    appliedOffer.textContent = 'Offer expired';
    appliedOffer.classList.remove('text-success');
    appliedOffer.classList.add('text-muted');
  }
}

function computeFinalPriceAndApplyOffer() {
  const total = Number(cartTotal.textContent) || 0;
  const diffSeconds = offerExpiresAt ? Math.floor((offerExpiresAt - Date.now())/1000) : -1;
  const offerValid = diffSeconds > 0;
  let final = total;
  if(offerValid && total > 0) {
    // apply 10% off
    final = Math.round(total * 0.90);
    appliedOffer.textContent = 'SHOESTOP10 — 10% off (auto applied)';
    appliedOffer.classList.add('text-success');
  } else {
    appliedOffer.textContent = 'No active offers';
    appliedOffer.classList.remove('text-success');
  }
  finalPriceEl.textContent = final;
  // also update displayed expected delivery
  expectedDelivery.textContent = computeExpectedDeliveryDate();
}

function computeExpectedDeliveryDate(daysFromNow = 3) {
  const d = new Date();
  d.setDate(d.getDate() + daysFromNow);
  return d.toLocaleDateString();
}

// Payment selection handlers
function clearPaymentSelection() {
  [payCardBtn, payCashBtn, payUpiBtn].forEach(b => { if(b) b.classList.remove('payment-selected'); });
}

// attach payment handlers (only if buttons exist)
function attachPaymentHandlers() {
  if(payCardBtn) payCardBtn.addEventListener('click', () => { clearPaymentSelection(); payCardBtn.classList.add('payment-selected'); });
  if(payCashBtn) payCashBtn.addEventListener('click', () => { clearPaymentSelection(); payCashBtn.classList.add('payment-selected'); });
  if(payUpiBtn) payUpiBtn.addEventListener('click', () => { clearPaymentSelection(); payUpiBtn.classList.add('payment-selected'); });
}

function placeOrderHandler() {
  // simple demo: pick payment method
  const method = (payCardBtn && payCardBtn.classList.contains('payment-selected')) ? 'Card' : ((payCashBtn && payCashBtn.classList.contains('payment-selected')) ? 'Cash' : ((payUpiBtn && payUpiBtn.classList.contains('payment-selected')) ? 'UPI' : null));
  if(!method) {
    alert('Please select a payment method.');
    return;
  }

  // simulate placing order
  const final = Number(finalPriceEl.textContent) || 0;
  const items = Object.entries(cart).map(([idStr, qty]) => {
    const id = Number(idStr);
    const p = products.find(x => x.id === id);
    return { id, title: p.title, price: p.price, qty };
  });

  // generate dummy delivery person
  const names = ['Ravi Kumar','Asha Devi','Suresh N.','Priya Singh'];
  const phones = ['+91 98 7654 3210', '+91 99 1122 3344', '+91 97 6543 2100', '+91 88 7766 5544'];
  const idx = Math.floor(Math.random()*names.length);
  if(deliveryName) deliveryName.textContent = names[idx];
  if(deliveryPhone) deliveryPhone.textContent = phones[idx];
  const deliveryDate = computeExpectedDeliveryDate(3);
  if(successDeliveryDate) successDeliveryDate.textContent = deliveryDate;

  // create order record and persist to localStorage
  const orders = JSON.parse(localStorage.getItem('shoe_orders') || '[]');
  const order = {
    id: 'ORD' + Date.now(),
    items,
    subtotal: Number(cartTotal.textContent) || 0,
    profile: getProfile(),
    finalPrice: final,
    paymentMethod: payCardBtn.classList.contains('payment-selected') ? 'Card' : (payCashBtn.classList.contains('payment-selected') ? 'Cash' : (payUpiBtn.classList.contains('payment-selected') ? 'UPI' : 'Unknown')),
    deliveryDate,
    deliveryName: names[idx],
    deliveryPhone: phones[idx],
    createdAt: new Date().toISOString()
  };
  orders.unshift(order);
  localStorage.setItem('shoe_orders', JSON.stringify(orders));

  // close checkout modal and show success
  if(checkoutModal && typeof checkoutModal.hide === 'function') checkoutModal.hide();
  if(orderSuccessModal && typeof orderSuccessModal.show === 'function') {
    // populate booked-by details into success modal
    const prof = getProfile();
    const bookedByNameEl = document.getElementById('bookedByName');
    const bookedByPhoneEl = document.getElementById('bookedByPhone');
    const bookedByAddressEl = document.getElementById('bookedByAddress');
    if(bookedByNameEl) bookedByNameEl.textContent = prof.name || '—';
    if(bookedByPhoneEl) bookedByPhoneEl.textContent = prof.phone || '—';
    if(bookedByAddressEl) bookedByAddressEl.textContent = prof.address || '—';
    orderSuccessModal.show();
  }

  // clear cart to mimic order placed
  clearCart();
}

// use a global error handler to surface runtime issues in console and an alert for dev
window.addEventListener('error', function (e) {
  console.error('Runtime error:', e.message, e.error);
  try { alert('Error: ' + e.message + '\nSee console for details.'); } catch (_) {}
});

// Orders modal: render saved orders
function renderOrders() {
  const orders = JSON.parse(localStorage.getItem('shoe_orders') || '[]');
  if(!orders || orders.length === 0) {
    ordersListEl.innerHTML = '<div class="text-muted text-center py-4">No orders yet.</div>';
    return;
  }
  const container = document.createElement('div');
  container.className = 'list-group';
  orders.forEach(o => {
    const item = document.createElement('div');
    item.className = 'list-group-item';
    item.innerHTML = `
      <div class="d-flex justify-content-between align-items-start">
        <div>
          <div class="fw-semibold">Order ${o.id}</div>
          <div class="small text-muted">Placed: ${new Date(o.createdAt).toLocaleString()}</div>
        </div>
        <div class="text-end">
          <div class="fw-semibold">₹${o.finalPrice}</div>
          <div class="small text-muted">Deliver by ${o.deliveryDate}</div>
        </div>
      </div>
      <div class="mt-2">
        ${o.items.map(it => `<div class="d-flex justify-content-between"><div>${escapeHtml(it.title)} × ${it.qty}</div><div>₹${it.price * it.qty}</div></div>`).join('')}
      </div>
      <div class="mt-2 small text-muted">Payment: ${o.paymentMethod} • Delivery: ${o.deliveryName} (${o.deliveryPhone})</div>
    `;
    container.appendChild(item);
  });
  ordersListEl.innerHTML = '';
  ordersListEl.appendChild(container);
}

// Profile save/load
function loadProfile() {
  const stored = JSON.parse(localStorage.getItem('shoe_profile') || '{}');
  const curEmail = getCurrentUserEmail();
  if(curEmail) {
    const user = findUserByEmail(curEmail) || {};
    profileName.value = user.name || stored.name || '';
    profileEmail.value = user.email || stored.email || '';
  } else {
    profileName.value = stored.name || '';
    profileEmail.value = stored.email || '';
  }
  profilePhone.value = stored.phone || '';
  profileAddress.value = stored.address || '';
}
// (profile save handler will be attached after DOM ready)

function clearCart() {
  cart = {};
  updateCartUI();
}

// Search and filter
function doSearchFilter() {
  const q = (searchInput.value || '').trim().toLowerCase();
  const cat = categorySelect.value;
  const filtered = products.filter(p => {
    const matchesQuery = q === '' ||
      p.title.toLowerCase().includes(q) ||
      p.category.toLowerCase().includes(q);

    const matchesCat = (cat === 'all') || (p.category === cat);
    return matchesQuery && matchesCat;
  });
  renderProducts(filtered);
}

// Attach events after DOM ready (see DOMContentLoaded below)

// init
document.addEventListener('DOMContentLoaded', () => {
  // assign DOM nodes
  productGrid = document.getElementById('productGrid');
  shownCount = document.getElementById('shownCount');
  cartCount = document.getElementById('cartCount');
  topCartCountEl = document.getElementById('topCartCount');
  topLeftCartBtn = document.getElementById('topLeftCartBtn');
  cartItems = document.getElementById('cartItems');
  cartTotal = document.getElementById('cartTotal');
  searchInput = document.getElementById('searchInput');
  searchBtn = document.getElementById('searchBtn');
  categorySelect = document.getElementById('categorySelect');
  clearCartBtn = document.getElementById('clearCartBtn');
  checkoutBtn = document.getElementById('checkoutBtn');
  yearSpan = document.getElementById('year');

  offerTimer = document.getElementById('offerTimer');
  checkoutTotal = document.getElementById('checkoutTotal');
  finalPriceEl = document.getElementById('finalPrice');
  appliedOffer = document.getElementById('appliedOffer');
  expectedDelivery = document.getElementById('expectedDelivery');
  placeOrderBtn = document.getElementById('placeOrderBtn');
  deliveryName = document.getElementById('deliveryName');
  deliveryPhone = document.getElementById('deliveryPhone');
  successDeliveryDate = document.getElementById('successDeliveryDate');

  saveProfileBtn = document.getElementById('saveProfileBtn');
  profileName = document.getElementById('profileName');
  profileEmail = document.getElementById('profileEmail');
  profilePhone = document.getElementById('profilePhone');
  profileAddress = document.getElementById('profileAddress');
  ordersListEl = document.getElementById('ordersList');

  addToastEl = document.getElementById('addToast');
  addToast = addToastEl ? new bootstrap.Toast(addToastEl) : null;

  // Auth elements
  const authModalElement = document.getElementById('authModal');
  const authBtnEl = document.getElementById('authBtn');
  const loginEmailEl = document.getElementById('loginEmail');
  const loginPasswordEl = document.getElementById('loginPassword');
  const loginBtnEl = document.getElementById('loginBtn');
  const registerNameEl = document.getElementById('registerName');
  const registerEmailEl = document.getElementById('registerEmail');
  const registerPasswordEl = document.getElementById('registerPassword');
  const registerBtnEl = document.getElementById('registerBtn');
  const logoutBtnEl = document.getElementById('logoutBtn');
  const authWelcomeEl = document.getElementById('authWelcome');
  const authUserNameEl = document.getElementById('authUserName');
  const authUserEmailEl = document.getElementById('authUserEmail');
  const loginMsgEl = document.getElementById('loginMsg');
  const registerMsgEl = document.getElementById('registerMsg');

  // Instantiate Bootstrap modal objects safely
  const orderSuccessEl = document.getElementById('orderSuccessModal');
  if(orderSuccessEl) orderSuccessModal = new bootstrap.Modal(orderSuccessEl);
  checkoutModalEl = document.getElementById('checkoutModal');
  if(checkoutModalEl) checkoutModal = new bootstrap.Modal(checkoutModalEl);

  // payment buttons
  payCardBtn = document.getElementById('payCardBtn');
  payCashBtn = document.getElementById('payCashBtn');
  payUpiBtn = document.getElementById('payUpiBtn');

  // booking/profile display in checkout
  const bookingForEl = document.getElementById('bookingFor');
  const editProfileFromCheckout = document.getElementById('editProfileFromCheckout');
  if(bookingForEl) {
    const p = getProfile();
    bookingForEl.textContent = p && p.name ? `${p.name} • ${p.phone || ''}` : 'Not set';
  }
  if(editProfileFromCheckout) {
    editProfileFromCheckout.addEventListener('click', () => {
      const profileModalEl = document.getElementById('profileModal');
      const m = new bootstrap.Modal(profileModalEl);
      m.show();
    });
  }

  // set year
  if(yearSpan) yearSpan.textContent = new Date().getFullYear();

  // render UI and attach event handlers
  renderProducts(products);
  updateCartUI();
  startOfferCountdown(600);

  // initialize auth UI state
  updateAuthUI();

  // when auth modal shows, clear messages and refresh UI
  if(authModalElement) {
    authModalElement.addEventListener('show.bs.modal', () => {
      if(loginMsgEl) loginMsgEl.textContent = '';
      if(registerMsgEl) registerMsgEl.textContent = '';
      // populate login email with previous current user if available
      const cur = getCurrentUserEmail();
      if(cur && loginEmailEl) loginEmailEl.value = cur;
      updateAuthUI();
    });
  }

  // Register
  if(registerBtnEl) registerBtnEl.addEventListener('click', () => {
    const name = registerNameEl ? registerNameEl.value.trim() : '';
    const email = registerEmailEl ? registerEmailEl.value.trim() : '';
    const pass = registerPasswordEl ? registerPasswordEl.value : '';
    const res = registerUser(name, email, pass);
    if(!res.ok) {
      if(registerMsgEl) registerMsgEl.textContent = res.msg;
      return;
    }
    // success: update UI, hide modal
    updateAuthUI();
    // refresh profile form from user
    try { loadProfile(); } catch(e){}
    const m = authModalElement ? bootstrap.Modal.getInstance(authModalElement) || new bootstrap.Modal(authModalElement) : null;
    if(m) m.hide();
  });

  // Login
  if(loginBtnEl) loginBtnEl.addEventListener('click', () => {
    const email = loginEmailEl ? loginEmailEl.value.trim() : '';
    const pass = loginPasswordEl ? loginPasswordEl.value : '';
    const res = loginUser(email, pass);
    if(!res.ok) {
      if(loginMsgEl) loginMsgEl.textContent = res.msg;
      return;
    }
    updateAuthUI();
    try { loadProfile(); } catch(e){}
    const m = authModalElement ? bootstrap.Modal.getInstance(authModalElement) || new bootstrap.Modal(authModalElement) : null;
    if(m) m.hide();
  });

  // Logout
  if(logoutBtnEl) logoutBtnEl.addEventListener('click', () => {
    logoutUser();
    updateAuthUI();
    try { loadProfile(); } catch(e){}
  });

  // attach search/filter handlers
  if(searchBtn) searchBtn.addEventListener('click', doSearchFilter);
  if(searchInput) searchInput.addEventListener('keyup', (e) => { if(e.key === 'Enter') doSearchFilter(); });
  if(categorySelect) categorySelect.addEventListener('change', doSearchFilter);

  if(clearCartBtn) clearCartBtn.addEventListener('click', () => { clearCart(); });

  // Make the top-left cart button always open the cart modal programmatically
  if(topLeftCartBtn) {
    topLeftCartBtn.addEventListener('click', (e) => {
      try {
        e.preventDefault();
        e.stopPropagation();
        // remove focus so it doesn't get hidden under other interactive UI
        topLeftCartBtn.blur();
        const cartModalEl = document.getElementById('cartModal');
        if(!cartModalEl) {
          console.warn('Cart modal element not found');
          return;
        }
        const inst = bootstrap.Modal.getOrCreateInstance(cartModalEl);
        inst.show();
      } catch (err) {
        console.error('Failed to open cart modal from top-left button', err);
      }
    });
  }

  if(checkoutBtn) {
    checkoutBtn.addEventListener('click', () => {
      if(Object.keys(cart).length === 0) {
        alert('Your cart is empty.');
        return;
      }
      computeFinalPriceAndApplyOffer();
      // the modal is triggered by data-bs attributes on the button
    });
  }

  // Ensure navbar toggler works even if CSS forced it visible: programmatic toggle fallback
  const navToggler = document.querySelector('.navbar-toggler');
  const navMenu = document.getElementById('navMenu');
  if(navToggler && navMenu) {
    navToggler.addEventListener('click', (e) => {
      // prevent interfering with bootstrap; provide fallback toggle
      try {
        const isOpen = navMenu.classList.contains('show');
        if(isOpen) {
          navMenu.classList.remove('show');
          navToggler.classList.add('collapsed');
          navToggler.setAttribute('aria-expanded', 'false');
        } else {
          navMenu.classList.add('show');
          navToggler.classList.remove('collapsed');
          navToggler.setAttribute('aria-expanded', 'true');
        }
      } catch (err) {
        console.error('Navbar toggle fallback failed', err);
      }
    });
  }

  // attach payment handlers and place order
  attachPaymentHandlers();
  if(placeOrderBtn) placeOrderBtn.addEventListener('click', placeOrderHandler);

  // continue booking button in success modal
  continueBookingBtn = document.getElementById('continueBookingBtn');
  if(continueBookingBtn) continueBookingBtn.addEventListener('click', () => {
    // hide success modal and scroll to products
    const successEl = document.getElementById('orderSuccessModal');
    const m = bootstrap.Modal.getInstance(successEl);
    if(m) m.hide();
    const productsEl = document.getElementById('products');
    if(productsEl) productsEl.scrollIntoView({ behavior: 'smooth' });
  });

  // load profile into form
  try { loadProfile(); } catch(e){}
  if(saveProfileBtn) saveProfileBtn.addEventListener('click', () => {
    const obj = { name: profileName.value.trim(), email: profileEmail.value.trim(), phone: profilePhone.value.trim(), address: profileAddress.value.trim() };
    localStorage.setItem('shoe_profile', JSON.stringify(obj));
    // if logged in, update the user's name in the users list too
    const cur = getCurrentUserEmail();
    if(cur) {
      const users = loadUsers();
      const uidx = users.findIndex(x => x.email.toLowerCase() === cur.toLowerCase());
      if(uidx >= 0) {
        users[uidx].name = obj.name || users[uidx].name;
        saveUsers(users);
      }
    }
    const profileModalEl = document.getElementById('profileModal');
    const m = bootstrap.Modal.getInstance(profileModalEl);
    if(m) m.hide();
  });

  // when orders modal opens, render orders
  const ordersModalEl = document.getElementById('ordersModal');
  if(ordersModalEl) ordersModalEl.addEventListener('show.bs.modal', renderOrders);
});
